import React from 'react'

export default  (props) => {
    //undefined array
    var numbers = []

    var {totalpostsInAPI, maxDisplayrecordsPerButton} = props

    var numberOfButtons = totalpostsInAPI / maxDisplayrecordsPerButton

    for(let i=1;i<=totalpostsInAPI;i++){
        numbers.push(i)//defined array
    }
    return (<>
    <div className="container p-5">
        <ul class = "pagination">
        {
             numbers.map((res, i)=> <li class="page-item">
            <a class="page-link" href="#">{res}</a>
            </li> )
            }
        
        </ul>
      
    </div>
    </>)

  
}
